import React from 'react';
import { motion } from 'framer-motion';
import places from '../utils/places';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMapMarkerAlt } from '@fortawesome/free-solid-svg-icons';

const cardVariants = {
  hidden: { opacity: 0, y: 30 },
  visible: (i) => ({
    opacity: 1,
    y: 0,
    transition: { delay: i * 0.15, type: 'spring', stiffness: 50 }
  }),
  hover: { scale: 1.05, boxShadow: '0 8px 15px rgba(0,0,0,0.3)' }
};

const LandingPage = ({ onSelectPlace }) => {
  return (
    <div className="p-4 max-w-7xl mx-auto">
      <h1 className="text-4xl font-bold mb-8 text-center">Explore Sri Lanka</h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {places.map((place, index) => (
          <motion.div
            key={place.id}
            className="bg-white dark:bg-gray-800 rounded-lg shadow-md cursor-pointer overflow-hidden"
            custom={index}
            initial="hidden"
            animate="visible"
            whileHover="hover"
            variants={cardVariants}
            onClick={() => onSelectPlace(place)}
          >
            <div className="relative h-48 w-full">
              <img
                src={place.image}
                alt={place.name}
                className="object-cover w-full h-full"
                loading="lazy"
              />
              <div className="absolute top-2 left-2 bg-red-600 text-white rounded-full p-1">
                <FontAwesomeIcon icon={faMapMarkerAlt} />
              </div>
            </div>
            <div className="p-4">
              <h2 className="text-xl font-semibold mb-1">{place.name}</h2>
              <p className="text-gray-600 dark:text-gray-300 text-sm">{place.description}</p>
            </div>
          </motion.div>
        ))}
      </div>
    </div>
  );
};

export default LandingPage;
